    public void loadData()
    {
    String sql = "SELECT `RefNo`, `Drug name`, `Drug Type`, `Quantity`, `Unit Price`, `Dose` FROM `pharmacy mangment`";
    try{
    pst = con.prepareStatement(sql);
    rs = pst.executeQuery();
    Object columnData[] = new Object[6];
    while(rs.next()) {
        columnData[0] = rs.getInt("RefNo");
        columnData[1] = rs.getString("Drug name");
        columnData[2] = rs.getString("Drug Type");
        columnData[3] = rs.getInt("Quantity");
        columnData[4] = rs.getInt("Unit Price");
        columnData[5] = rs.getInt("Dose");
        defaultTableModel.addRow(columnData);
    }
}
       catch (SQLException e) {
            JOptionPane.showMessageDialog(null, e);
        }
            txtref.setText(null);
            cbose1.setSelectedIndex(-1);
            cbose2.setSelectedIndex(-1);
            txtquantity.setText(null);
            txtup.setText(null);
            txtdose.setText(null);
         
    
    }


