    private void btnaddMouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_btnaddMouseClicked
        int ref = Integer.parseInt(txtref.getText());
        String dname = cbose1.getSelectedItem().toString();
        String dtype = cbose2.getSelectedItem().toString();
        int quantity = Integer.parseInt(txtquantity.getText());
        int uprice = Integer.parseInt(txtup.getText());
        int dose = Integer.parseInt(txtdose.getText());
        if( dname.equals("") || dtype.equals(""))
            {
            
                JOptionPane.showMessageDialog(null, "Complete Your Information", "Missing Information", 2);
                
            }
        else{
            String sql = "INSERT INTO `pharmacy mangment`(`RefNo`, `Drug name`, `Drug Type`, `Quantity`, `Unit Price`, `Dose`) VALUES (?,?,?,?,?,?)";
            if(con != null){
        try {
   
           pst = con.prepareStatement(sql);
           pst.setInt(1, Integer.parseInt(txtref.getText()));
           pst.setString(2, (String) cbose1.getSelectedItem());
           pst.setString(3, (String) cbose2.getSelectedItem());
           pst.setInt(4,Integer.parseInt(txtquantity.getText()));
           pst.setInt(5,Integer.parseInt(txtup.getText()));
           pst.setInt(6,Integer.parseInt(txtdose.getText()));
            
            pst.execute();
            defaultTableModel.getDataVector().removeAllElements();
                    defaultTableModel.fireTableDataChanged();
                    loadData();
                    this.dispose();
                    new PharmacyManagement().setVisible(true);
                    JOptionPane.showMessageDialog(null, "Your Data updated Successfully !");
            
    }           catch (SQLException e) {
                    JOptionPane.showMessageDialog(null, e);
                    
                }
    
        }
        }

    }//GEN-LAST:event_btnaddMouseClicked

